INSERT INTO public.success_stories (student_name, company_name, experience_text, program_type, display_order) VALUES
('Mohammed Alami', 'Microsoft', 'My Co-op experience at Microsoft completely changed my perspective on software development. The mentorship and real-world projects prepared me for my full-time role after graduation.', 'Co-op', 1),
('Lina Benali', 'Deloitte', 'The Co-op program gave me confidence and practical skills that set me apart from other graduates. I received three job offers before graduation!', 'Co-op', 2),
('Omar El Fassi', 'OCP Group', 'Working at OCP during my Co-op placement taught me how to apply theoretical knowledge in practice. The experience was invaluable for my career development.', 'Co-op', 3),
('Salma Idrissi', 'Attijariwafa Bank', 'The Co-op program opened doors I never imagined. The networking opportunities and professional experience helped me land my dream job in banking.', 'Co-op', 4);
INSERT INTO public.success_stories (student_name, company_name, experience_text, program_type, display_order) VALUES
('Youssef Tazi', 'Capgemini', 'Remote@AUI allowed me to work flexibly while maintaining my studies. The project-based approach taught me excellent time management skills.', 'Remote@AUI', 1),
('Aicha Bennani', 'IBM', 'The remote work experience prepared me perfectly for the modern workplace. I learned to collaborate effectively across different time zones.', 'Remote@AUI', 2),
('Karim Lahlou', 'Accenture', 'Remote@AUI gave me the flexibility I needed while providing real industry experience. The faculty support was exceptional throughout.', 'Remote@AUI', 3);
INSERT INTO public.success_stories (student_name, company_name, experience_text, program_type, display_order) VALUES
